public class ClassCodeEditorAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		ClassCodeEditorForm classCodeEditorForm = (ClassCodeEditorForm) form;// TODO Auto-generated method stub
		try{
			PrintWriter out = response.getWriter();
			try{
				String oper = request.getParameter("oper");
				com.sanqing.bll.BClassCode B_V = new com.sanqing.bll.BClassCode();
				if(oper.equals("add"))
				{
					B_V.ClasscodeAdd(classCodeEditorForm);
				}
				else if(oper.equals("edit"))
				{
					B_V.ClasscodeEdit(classCodeEditorForm);
				}
				else if(oper.equals("delete"))
				{
					B_V.ClasscodeDelete(classCodeEditorForm.getProd_id());
				}
				out.print(B_V.ClasscodeConvertToHTMLTable());	
				
			}
			catch(Exception e){
				out.print(e.getMessage());
			}
		
		}catch(IOException e1){
			e1.printStackTrace();
		}
		return null;
	}
}